CREATE OR REPLACE FUNCTION lic_obter_se_reg_preco ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



nr_seq_mod_compra_w			bigint;
ie_registro_preco_w				varchar(1) := 'N';

BEGIN

select	coalesce(nr_seq_mod_compra,0)
into STRICT	nr_seq_mod_compra_w
from	reg_licitacao
where	nr_sequencia = nr_sequencia_p;

if (nr_seq_mod_compra_w <> 0) then
	select	coalesce(CASE WHEN ie_registro_contrato='R' THEN 'S'  ELSE 'N' END ,'S')
	into STRICT	ie_registro_preco_w
	from	reg_lic_mod_compra
	where	nr_sequencia = nr_seq_mod_compra_w;
end if;

return	ie_registro_preco_w;

end;
