CREATE OR REPLACE FUNCTION lic_obter_se_todos_itens_homol ( nr_seq_licitacao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_lic_item_w		bigint;
qt_existe_w		bigint;
ds_retorno_w		varchar(1);

c01 CURSOR FOR
SELECT	nr_seq_lic_item
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p;


BEGIN

ds_retorno_w	:= 'S';

open c01;
loop
fetch c01 into
	nr_seq_lic_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	reg_lic_homol_itens a
	where	a.nr_seq_lic_item		= nr_seq_lic_item_w
	and	nr_seq_licitacao		= nr_seq_licitacao_p
	and	coalesce(a.ie_homologado,'N')	= 'S';

	if (qt_existe_w = 0) then
		ds_retorno_w	:= 'N';
	end if;

	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
