SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_solic_pendente ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_item_w			double precision := 0;
qt_item_ww			double precision := 0;
nr_solic_compra_w			bigint;
nr_item_solic_compra_w		integer;
qt_cancelado_w			double precision;

c01 CURSOR FOR 
SELECT	a.nr_solic_compra, 
	a.nr_item_solic_compra, 
	coalesce(sum(a.qt_item),0) 
from	reg_lic_item_solic a, 
	reg_lic_item b, 
	solic_compra c 
where	a.nr_seq_lic_item	= b.nr_seq_lic_item 
and	a.nr_seq_licitacao	= b.nr_seq_licitacao 
and	a.nr_solic_compra	= c.nr_solic_compra 
and	a.nr_seq_licitacao	= nr_seq_licitacao_p 
and	b.nr_seq_lic_item	= nr_seq_lic_item_p 
and	(c.dt_autorizacao IS NOT NULL AND c.dt_autorizacao::text <> '') 
and not exists ( 
	SELECT	  1 
	from	ordem_compra x, 
		ordem_compra_item y 
	where	x.nr_ordem_compra		= y.nr_ordem_compra 
	and	y.nr_solic_compra		= a.nr_solic_compra 
	and	y.nr_item_solic_compra	= a.nr_item_solic_compra 
	and	(x.dt_aprovacao IS NOT NULL AND x.dt_aprovacao::text <> '')) 
group by	a.nr_solic_compra, 
	a.nr_item_solic_compra;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_solic_compra_w, 
	nr_item_solic_compra_w, 
	qt_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(sum(c.qt_prevista_entrega),0) 
	into STRICT	qt_cancelado_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_item_oci		= b.nr_item_oci 
	and	b.nr_solic_compra		= nr_solic_compra_w 
	and	b.nr_item_solic_compra	= nr_item_solic_compra_w 
	and	((a.nr_seq_motivo_cancel IS NOT NULL AND a.nr_seq_motivo_cancel::text <> '') or (c.dt_cancelamento IS NOT NULL AND c.dt_cancelamento::text <> ''));
	 
	qt_item_ww := qt_item_ww + qt_item_w - qt_cancelado_w;
	 
	end;
end loop;
close C01;
 
return	qt_item_ww;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
