CREATE OR REPLACE FUNCTION lic_obter_valor_vencedor_item ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_vencedor_w			double precision;
nr_lote_compra_w		varchar(80);


BEGIN

select	coalesce(max(vl_item),0)
into STRICT	vl_vencedor_w
from	reg_lic_vencedor
where	nr_seq_licitacao	= nr_seq_licitacao_p
and	nr_seq_lic_item	= nr_seq_lic_item_p;

if (vl_vencedor_w = 0) then

	select	max(nr_lote_compra)
	into STRICT	nr_lote_compra_w
	from	reg_lic_item
	where	nr_seq_licitacao	= nr_seq_licitacao_p
	and	nr_seq_lic_item	= nr_seq_lic_item_p;

	if (nr_lote_compra_w IS NOT NULL AND nr_lote_compra_w::text <> '') then

		select	max(lic_obter_vl_fim_item_lote(nr_seq_licitacao, nr_seq_lic_item,  lic_obter_forn_vencedor_item(nr_seq_licitacao, nr_seq_lic_item)))
		into STRICT	vl_vencedor_w
		from	reg_lic_item
		where	nr_seq_licitacao	= nr_seq_licitacao_p
		and	nr_lote_compra		= nr_lote_compra_w
		and	nr_seq_lic_item		= nr_seq_lic_item_p
		and      coalesce(ie_lote,'N')	= 'N';
	end if;
end if;

return	vl_vencedor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
