SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_vigencia_reg_compra ( nr_seq_licitacao_p bigint, ie_data_p text) RETURNS timestamp AS $body$
DECLARE



/*ie_data_p
I	Data inicio vigencia
F	Data fim vigencia*/
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w	timestamp;
dt_retorno_w		timestamp;

c01 CURSOR FOR
SELECT	dt_inicio_vigencia,
	dt_fim_vigencia
from	reg_compra
where	nr_seq_licitacao = nr_seq_licitacao_p
and	trunc(clock_timestamp(),'dd') between trunc(dt_inicio_vigencia,'dd') and trunc(dt_fim_vigencia,'dd')
order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	dt_inicio_vigencia_w,
	dt_fim_vigencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	dt_inicio_vigencia_w	:= dt_inicio_vigencia_w;
	dt_fim_vigencia_w	:= dt_fim_vigencia_w;
	end;
end loop;
close C01;

if (ie_data_p = 'I') then
	dt_retorno_w := dt_inicio_vigencia_w;
elsif (ie_data_p = 'F') then
	dt_retorno_w := dt_fim_vigencia_w;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
