CREATE OR REPLACE FUNCTION lic_obter_vl_limite_conta ( dt_referencia_p timestamp, cd_conta_contabil_p text, nr_seq_tipo_compra_p bigint, nr_seq_mod_compra_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_limite_w	double precision;


BEGIN

select	coalesce(max(a.vl_limite),0)
into STRICT	vl_limite_w
from	reg_lic_conta_valor a
where	dt_referencia_p between a.dt_inicio_vigencia and a.dt_fim_vigencia
and	a.ie_situacao = 'A'
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(a.cd_conta_contabil, coalesce(cd_conta_contabil_p, 0))		= coalesce(cd_conta_contabil_p,0)
and	coalesce(a.nr_seq_tipo_compra, coalesce(nr_seq_tipo_compra_p,0))		= coalesce(nr_seq_tipo_compra_p,0)
and	coalesce(a.nr_seq_mod_compra, coalesce(nr_seq_mod_compra_p,0))		= coalesce(nr_seq_mod_compra_p,0);

return	vl_limite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
