CREATE OR REPLACE FUNCTION lic_obter_vl_pesquisa_preco (nr_seq_licitacao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_preco_liq_w	double precision;


BEGIN

select	sum(b.vl_preco_liquido)
into STRICT	vl_preco_liq_w
from	cot_compra_forn a,
	cot_compra_forn_item b,
	cot_compra_item c
where	a.nr_sequencia = b.nr_seq_cot_forn
and	c.nr_item_cot_compra = b.nr_item_cot_compra
and	c.nr_cot_compra = b.nr_cot_compra
and	c.nr_seq_lic_item > 0
and	a.nr_cot_compra in (SELECT	x.nr_cot_compra
			from	cot_compra x
			where	x.nr_seq_reg_licitacao = nr_seq_licitacao_p);

return	vl_preco_liq_w;

end;
