CREATE OR REPLACE FUNCTION lic_obter_vl_prop_item_lote ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, nr_seq_fornec_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision := 0;
nr_seq_lote_w			integer;
nr_lote_compra_w			varchar(80);
vl_original_w			double precision;
pr_diferenca_w			double precision;
vl_maximo_edital_w			double precision;
vl_maximo_edital_lote_w		double precision;
vl_diferenca_w			double precision;


BEGIN

select	coalesce(max(nr_seq_lote),0),
	max(vl_maximo_edital)
into STRICT	nr_seq_lote_w,
	vl_maximo_edital_w
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p;

if (nr_seq_lote_w > 0) then

	select	vl_maximo_edital
	into STRICT	vl_maximo_edital_lote_w
	from	reg_lic_item
	where	nr_seq_licitacao = nr_seq_licitacao_p
	and	nr_seq_lic_item = nr_seq_lote_w;

	select	coalesce(max(vl_original),0)
	into STRICT	vl_original_w
	from	reg_lic_item_fornec
	where	nr_seq_fornec = nr_seq_fornec_p
	and	nr_seq_lic_item = nr_seq_lote_w;

	pr_diferenca_w	:= dividir((vl_original_w * 100), vl_maximo_edital_lote_w);
	pr_diferenca_w	:= pr_diferenca_w - 100;

	vl_diferenca_w	:= abs(dividir((vl_maximo_edital_w * pr_diferenca_w),100));

	if (vl_original_w >= vl_maximo_edital_lote_w) then
		vl_retorno_w	:= vl_maximo_edital_w + vl_diferenca_w;
	else
		vl_retorno_w	:= vl_maximo_edital_w - vl_diferenca_w;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
