CREATE OR REPLACE FUNCTION lic_obter_vl_reg_preco_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_licitacao_w		bigint;
nr_seq_lic_item_w		bigint;
vl_item_w			double precision;


BEGIN
select	coalesce(max(nr_seq_licitacao), 0),
	coalesce(max(nr_seq_lic_item), 0)
into STRICT	nr_seq_licitacao_w,
	nr_seq_lic_item_w
from	reg_lic_item_solic
where	nr_solic_compra	  	= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p;

if (nr_seq_licitacao_w > 0) and (nr_seq_lic_item_w > 0) then

	select	coalesce(max(vl_item),0)
	into STRICT	vl_item_w
	from	reg_compra_item
	where	nr_seq_licitacao = nr_seq_licitacao_w
	and	nr_seq_lic_item = nr_seq_lic_item_w;

	if (vl_item_w = 0) then
		select	lic_obter_valor_vencedor_item(nr_seq_licitacao_w, nr_seq_lic_item_w)
		into STRICT	vl_item_w
		;
	end if;

end if;

return	vl_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
