CREATE OR REPLACE FUNCTION lic_obter_vl_total_lic_item (nr_seq_licitacao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_w			double precision;


BEGIN

select	coalesce(sum(qt_item * vl_maximo_edital),0)
into STRICT	vl_total_w
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


return	vl_total_w;

end;
