CREATE OR REPLACE FUNCTION lote_ent_obter_ds_inst_instr (nr_seq_instrucao_p bigint, nr_seq_instituicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

if (nr_seq_instrucao_p IS NOT NULL AND nr_seq_instrucao_p::text <> '') then

	select	max(ds_instrucao)
	into STRICT	ds_retorno_w
	from	lote_ent_inst_instrucao
	where	nr_sequencia = nr_seq_instrucao_p
	and	nr_seq_instituicao = nr_seq_instituicao_p
	and	ie_situacao = 'A';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
