CREATE OR REPLACE FUNCTION lote_ent_obter_instr_int ( nr_seq_instituicao_p bigint, cd_tipo_instrucao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*
cd_tipo_instrucao:
B - Busca ativa
S - Secretaria
A - Ambos
*/
BEGIN

if (nr_seq_instituicao_p IS NOT NULL AND nr_seq_instituicao_p::text <> '') and (cd_tipo_instrucao_p IS NOT NULL AND cd_tipo_instrucao_p::text <> '') then

	select 	substr(max(a.DS_INSTRUCAO),1,255)
	into STRICT	ds_retorno_w
	from	lote_ent_inst_instrucao a
	where	a.nr_seq_instituicao = nr_seq_instituicao_p
	and		((cd_tipo_instrucao = cd_tipo_instrucao_p) or (cd_tipo_instrucao = 'A'));

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
