CREATE OR REPLACE FUNCTION lote_ent_obter_se_lote_vazio (nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


qt_fichas_w		bigint;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then

	select 	count(*)
	into STRICT	qt_fichas_w
	from	lote_ent_secretaria a,
		lote_ent_sec_ficha b
	where	a.nr_sequencia = b.nr_seq_lote_sec
	and	a.nr_sequencia = nr_seq_lote_p;

end if;

return	qt_fichas_w;

end;
$body$
LANGUAGE PLPGSQL
