SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lote_ent_retorna_status (nr_seq_ficha_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
nr_prescricao_w			bigint;
ie_status_atend_w		varchar(10);
nr_atendimento_w		bigint;
				

BEGIN 
 
/*select 	max(nr_prescricao) 
into	nr_prescricao_w 
from	lote_ent_sec_ficha 
where	nr_sequencia = nr_seq_ficha_p;*/
 
 
select	max(a.nr_atendimento) 
into STRICT	nr_atendimento_w 
from	atendimento_paciente a 
where	a.nr_seq_ficha_lote_ant = nr_seq_ficha_p;
 
select	max(a.nr_prescricao) 
into STRICT	nr_prescricao_w 
from	prescr_medica a 
where	a.nr_atendimento = nr_atendimento_w;
 
select	max(a.ie_status_atend) 
into STRICT	ie_status_atend_w 
from	prescr_procedimento a, 
		lote_ent_sec_ficha b 
where	a.nr_prescricao = b.nr_prescricao 
and		a.nr_prescricao = nr_prescricao_w 
and		a.nr_seq_exame = nr_seq_exame_p;
	 
if (ie_status_atend_w IS NOT NULL AND ie_status_atend_w::text <> '') then 
 
	select	coalesce(substr(Obter_regra_exame_status(vl_dominio),1,255),ds_valor_dominio) 
	into STRICT	ds_retorno_w 
	from	valor_dominio 
	where	cd_dominio = 1030 
	and		vl_dominio = ie_status_atend_w;
	 
end if;	
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION lote_ent_retorna_status (nr_seq_ficha_p bigint, nr_seq_exame_p bigint) FROM PUBLIC;

