SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lote_ent_verifica_exames_bloq ( nr_seq_ficha_p bigint, ds_lista_exames_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_w			bigint;
cd_material_exame_w		varchar(20);
cd_pessoa_fisica_w		varchar(10);
nr_prescricao_w			bigint;
nr_seq_lote_w			bigint;
qt_exame_sem_result_w			bigint;
qt_total_exame_w			bigint;
ds_mensagem_retorno_w		varchar(255);
nr_seq_prescr_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_exame,
		a.cd_material_exame,
		c.nr_sequencia
	from	lote_ent_sec_ficha_exam a,
		lote_ent_sec_ficha b,
		prescr_procedimento c
	where	a.nr_seq_ficha = nr_seq_ficha_p
	and	a.nr_seq_ficha = b.nr_sequencia
	and	b.nr_prescricao = c.nr_prescricao
	and	c.nr_seq_exame = a.nr_seq_exame
	and	obter_se_contido(a.nr_sequencia,ds_lista_exames_p) = 'S';


BEGIN

qt_total_exame_w := 0;

select	max(a.nr_seq_lote_sec),
	max(a.cd_pessoa_fisica),
	max(a.nr_prescricao)
into STRICT	nr_seq_lote_w,
	cd_pessoa_fisica_w,
	nr_prescricao_w
from	lote_ent_sec_ficha a
where	a.nr_sequencia = nr_seq_ficha_p;

open C01;
loop
fetch C01 into
	nr_seq_exame_w,
	cd_material_exame_w,
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	SELECT 	COUNT(*)
	INTO STRICT	qt_exame_sem_result_w
	FROM	exame_lab_result_item a,
		exame_lab_resultado b,
		prescr_procedimento c
	WHERE	a.nr_seq_prescr = c.nr_sequencia
	AND	a.nr_seq_resultado = b.nr_seq_resultado
	and b.nr_prescricao = c.nr_prescricao
	AND	c.nr_prescricao = nr_prescricao_w
	AND	c.nr_sequencia = nr_seq_prescr_w
	AND	((coalesce(a.ds_resultado::text, '') = '') AND (coalesce(a.qt_resultado::text, '') = '') AND (coalesce(a.pr_resultado::text, '') = ''))
	AND coalesce(c.ie_exame_bloqueado,'N') = 'N'
	AND	(a.nr_seq_material IS NOT NULL AND a.nr_seq_material::text <> '');

	if (qt_exame_sem_result_w > 0) then
		qt_total_exame_w := qt_total_exame_w + 1;
	end if;

	end;
end loop;
close C01;

if (qt_total_exame_w > 0) then
	ds_mensagem_retorno_w := Wheb_mensagem_pck.get_texto(308772); --'Existem exames sem resultado nas fichas';
else
	ds_mensagem_retorno_w := '';
end if;

return ds_mensagem_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
