CREATE OR REPLACE FUNCTION macros_envio_email_material (ie_tipo_mensagem_p bigint) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w				varchar(255);

/*		Macros disponiveis

CONTA
ATENDIMENTO
DATALANCAMENTO
PACIENTE
CONVENIO
QUANTIDADE
FORNECEDOR
ALTERACOES
*/
BEGIN

if (ie_tipo_mensagem_p = 1) then
	ds_mensagem_w :=	'@CONTA' || chr(13) ||chr(10) ||
				'@ATENDIMENTO' || chr(13) ||chr(10) ||
				'@DATALANCAMENTO' || chr(13) ||chr(10) ||
				'@PACIENTE' || chr(13) ||chr(10) ||
				'@CONVENIO' || chr(13) ||chr(10) ||
				'@QUANTIDADE' || chr(13) ||chr(10) ||
				'@FORNECEDOR' || chr(13) ||chr(10) ||
				'@DESCMAT' || chr(13) ||chr(10) ||
				'@CODMAT';
elsif (ie_tipo_mensagem_p = 3) then
	ds_mensagem_w :=	'@DESCMAT' || chr(13) ||chr(10) ||
				'@CODMAT'  || chr(13) ||chr(10) ||
				'@DESCFAMILIAMAT' || chr(13) ||chr(10) ||
				'@ALTERACOES';
elsif (ie_tipo_mensagem_p in (2,4,5,8)) then
	ds_mensagem_w :=	'@DESCMAT' || chr(13) ||chr(10) ||
				'@CODMAT'  || chr(13) ||chr(10) ||
				'@DESCFAMILIAMAT';
elsif (ie_tipo_mensagem_p = 6) then
	ds_mensagem_w :=	'@FORNECEDOR' || chr(13) ||chr(10) ||
				'@CODMAT'  || chr(13) ||chr(10) ||
				'@ACAO' || chr(13) ||chr(10) ||
				'@OPERACAO'  || chr(13) ||chr(10) ||
				'@ORIGEMDOC'  || chr(13) ||chr(10) ||
				'@LOCALESTOQUE'  || chr(13) ||chr(10) ||
				'@LOCALDESTINO'  || chr(13) ||chr(10) ||
				'@SETOR' || chr(13) ||chr(10) ||
				'@DTMOVIMENTO'  || chr(13) ||chr(10) ||
				'@NRMOVIMENTO'  || chr(13) ||chr(10) ||
				'@USUARIO' || chr(13) ||chr(10) ||
				'@QTMOVIMENTO' || chr(13) ||chr(10) ||
				'@QTESTOQUE';
elsif (ie_tipo_mensagem_p = 7) then
	ds_mensagem_w :=	'@DESCMAT' || chr(13) ||chr(10) ||
				'@CODMAT';
end if;

return	ds_mensagem_w;

end;
