CREATE OR REPLACE FUNCTION manchester_obter_ie_retriagem (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq			bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select		coalesce(max(ie_retriagem),'N'),
			coalesce(max(c.nr_sequencia), 0)
	into STRICT		ds_retorno_w,
			nr_seq
	from 		triagem_pronto_atend c
	where		c.nr_atendimento = nr_atendimento_p;
end if;


return	ds_retorno_w;

end;
