CREATE OR REPLACE FUNCTION manchester_obter_se_permite (nr_atend_p bigint, cd_estabelecimento_p bigint, cd_setor_atend_p bigint, ie_tipo_atend_p bigint, ie_retriagem_p text) RETURNS char AS $body$
DECLARE


ie_result_w			    char(1) := 'N';
cd_estabelecimento_w 	smallint;
cd_setor_atendimento_w 	integer;
ie_tipo_atendimento_w	smallint;
ds_triagem_W  		varchar(60);
dt_alta_w		timestamp;
dt_alta_medico_w	timestamp;


BEGIN
select	max(a.cd_estabelecimento),
		max(b.cd_setor_atendimento),
		max(a.ie_tipo_atendimento),
		max(SUBSTR(coalesce(obter_desc_triagem(a.nr_seq_triagem), obter_triagem_atendimento(a.nr_atendimento)),1,60)),
		Max(a.dt_alta),
		max(a.dt_alta_medico)
into STRICT
		cd_estabelecimento_w,
		cd_setor_atendimento_w,
		ie_tipo_atendimento_w,
		ds_triagem_W,
		dt_alta_w,
		dt_alta_medico_w
from	atendimento_paciente a,
		atend_paciente_unidade b
WHERE	a.nr_atendimento 	= b.nr_atendimento
and		a.nr_atendimento 	= nr_atend_p
and		dt_entrada_unidade = (SELECT max(dt_entrada_unidade) from atend_paciente_unidade c where c.nr_atendimento = a.nr_atendimento);


if (cd_estabelecimento_w = cd_estabelecimento_p) then
	if	((cd_setor_atendimento_w = cd_setor_atend_p) or (cd_setor_atend_p = 0)) then
		if	((ie_tipo_atendimento_w = ie_tipo_atend_p) or (ie_tipo_atend_p = 0)) then
			if	(((ie_retriagem_p = 'N')AND (coalesce(ds_triagem_W::text, '') = '')) OR (ie_retriagem_p = 'S' AND ds_triagem_W IS NOT NULL AND ds_triagem_W::text <> '') OR (ie_retriagem_p = 'A'))then
				if (coalesce(dt_alta_w::text, '') = '') and (coalesce(dt_alta_medico_w::text, '') = '') then
					ie_result_w	:= 'S';
				end if;
			end if;
		end if;
	end if;
end if;

return	ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
