SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_consistir_xml_os ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
ds_lista_xml_w		varchar(255);
ds_carac_xml_w		varchar(15);
ds_texto_w		varchar(32767);
ds_texto_aux_w		varchar(32767);
ds_texto_long_w		text;

/*	ie_opcao_p
	H - Históricos da ordem de serviço
	A - Anexos da ordem de serviço	*/
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	if (ie_opcao_p = 'H') then
		begin
		ds_lista_xml_w := ';]]>;';

		begin
		select	ds_relat_tecnico
		into STRICT	ds_texto_long_w
		from	man_ordem_serv_tecnico
		where	nr_sequencia = nr_sequencia_p;
		exception
		when others then
			ds_texto_long_w := '';
		end;

		ds_texto_w := substr(ds_texto_long_w,1,32767);
		end;
	elsif (ie_opcao_p = 'A') then
		begin
		ds_lista_xml_w := ';<;>;&;|;*;?;";';

		begin
		select	ds_arquivo
		into STRICT	ds_texto_w
		from	man_ordem_serv_arq
		where	nr_sequencia = nr_sequencia_p;
		exception
		when others then
			ds_texto_w := '';
		end;
		end;
	end if;

	if (instr(coalesce(ds_lista_xml_w,'X'),';',1,2) > 1) and (coalesce(ds_texto_w,'X') <> 'X') then
		begin
		ds_texto_aux_w	:= ds_texto_w;

		for i in 1..length(ds_lista_xml_w)-length(replace(ds_lista_xml_w,';',''))-1 loop
			begin
			ds_carac_xml_w	:= substr(ds_lista_xml_w,instr(ds_lista_xml_w,';',1,i)+1,instr(ds_lista_xml_w,';',1,i+1)-(instr(ds_lista_xml_w,';',1,i)+1));
			ds_texto_aux_w	:= replace(ds_texto_aux_w,ds_carac_xml_w,'');
			end;
		end loop;

		if (ds_texto_w <> coalesce(ds_texto_aux_w,'X')) then
			ie_retorno_w := 'S';
		end if;
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
