SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_consulta_data_expwheb (dt_versao_destino_w text) RETURNS varchar AS $body$
DECLARE


cd_versao_destino_w	varchar(500);
ds_retorno_w		varchar(500);


BEGIN

select	max(a.cd_versao)
into STRICT	cd_versao_destino_w
from	aplicacao_tasy_versao a
where	trunc(dt_versao) <= trunc(to_date(dt_versao_destino_w,'DDMMYYYY'))
and		upper(cd_aplicacao_tasy) = 'TASY'
and	trunc(dt_atualizacao) > clock_timestamp() - interval '200 days';

select	a.cd_versao || ';'||a.qt_tab_exportada
into STRICT	ds_retorno_w
from	aplicacao_tasy_versao a
where cd_versao = cd_versao_destino_w
and		upper(cd_aplicacao_tasy) = 'TASY';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
