CREATE OR REPLACE FUNCTION man_especif_obter_se_test_anex ( nr_seq_ordem_serv_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
ie_classificacao_w		varchar(1);
nr_seq_especif_teste_w		bigint;
ie_possui_anexo_w		varchar(1);
nr_seq_especif_w		bigint;
nr_seq_teste_caso_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	man_os_especif_teste_caso
where	nr_seq_especif_teste = nr_seq_especif_teste_w;


BEGIN

	select	ie_classificacao
	into STRICT	ie_classificacao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_p;

	if (ie_classificacao_w = 'S') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_especif_w
		from	man_os_especificacao
		where	nr_seq_ordem_servico = nr_seq_ordem_serv_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

		if (nr_seq_especif_w IS NOT NULL AND nr_seq_especif_w::text <> '') then

			select	max(nr_sequencia)
			into STRICT	nr_seq_especif_teste_w
			from	man_os_especif_teste
			where	nr_seq_especificacao = nr_seq_especif_w;

			open c01;
			loop
			fetch c01 into
				nr_seq_teste_caso_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

				select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
				into STRICT	ie_possui_anexo_w
				from	man_os_teste_evidencia
				where	nr_seq_teste_caso = nr_seq_teste_caso_w;

				if (ie_possui_anexo_w = 'N') then
					ds_retorno_w := 'N';
				end if;

			end;
			end loop;
			close c01;

		end if;

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
