ds_retorno_w		varchar(4000) := null;


BEGIN

	select	coalesce(max(substr(obter_desc_expressao(ms.cd_exp_descricao), 1, 4000)), '')
	into STRICT	ds_retorno_w
	from	man_severidade ms
	where	ms.nr_sequencia = nr_seq_severity_p;

	return ds_retorno_w;
	
end;
