CREATE OR REPLACE FUNCTION man_get_priority ( ie_severity_p text ) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000) := null;


BEGIN

	select	case	when	ie_severity_p = 1	then	'E'
			when	ie_severity_p = 2	then	'E'
			when	ie_severity_p = 3	then	'A'
			when	ie_severity_p = 4	then	'M'
			when	ie_severity_p = 5	then	'B'
			else					'B'
		end ie_priority
	into STRICT	ds_retorno_w
	;	

	return ds_retorno_w;
	
end;
$body$
