CREATE OR REPLACE FUNCTION man_obter_anexos_os_servico ( nr_seq_servico_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_doc_w	bigint;
ds_arquivo_w	servico_anexo.ds_arquivo%type;
ds_documento_w	servico_anexo.ds_arquivo%type;
ds_retorno_w	varchar(32000);

C01 CURSOR FOR
	SELECT	nr_seq_doc,
		ds_arquivo
	from	servico_anexo
	where	nr_seq_servico = nr_seq_servico_p
	order by 1;

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_doc_w,
	ds_arquivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (nr_seq_doc_w IS NOT NULL AND nr_seq_doc_w::text <> '') then
			begin
				select	ds_arquivo
				into STRICT	ds_documento_w
				from	qua_documento
				where	nr_sequencia = nr_seq_doc_w
				and	ie_situacao = 'A';

				ds_retorno_w := ds_retorno_w || ';' || ds_documento_w;
			end;
		end if;

		if (ds_arquivo_w IS NOT NULL AND ds_arquivo_w::text <> '') then
			begin
				ds_retorno_w := ds_retorno_w || ';' || ds_arquivo_w;
			end;
		end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
