SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_atividade_exec ( nm_usuario_exec_p text, nr_seq_funcao_p bigint, dt_atividade_p timestamp) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;
dt_atividade_inicial_w	timestamp;
dt_atividade_final_w	timestamp;


BEGIN

dt_atividade_inicial_w	:= trunc(dt_atividade_p,'dd');
dt_atividade_final_w	:= fim_dia(dt_atividade_p);

select	sum(a.qt_minuto)
into STRICT	ds_retorno_w
from	man_ordem_serv_ativ a
where	a.nm_usuario_exec = nm_usuario_exec_p
and	a.nr_seq_funcao	= nr_seq_funcao_p
and	a.dt_atividade between dt_atividade_inicial_w and dt_atividade_final_w;

return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
