CREATE OR REPLACE FUNCTION man_obter_baixa_os_dia ( dt_referencia_p timestamp, ie_estagio_p text) RETURNS bigint AS $body$
DECLARE


qt_baixa_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_baixa_w
from	man_ordem_serv_hist a
where	dt_referencia		= dt_referencia_p - 1
and	ie_estagio		= ie_estagio_p
and	not	exists (
	SELECT 1
	from	man_ordem_serv_hist b
	where	a.nr_seq_ordem	= b.nr_seq_ordem
	and	b.ie_estagio		= a.ie_estagio
	and	b.dt_referencia	= dt_referencia_p);

Return qt_baixa_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
