SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_codigo_solic_os_par ( ds_informacao_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
ie_forma_inf_solic_w	varchar(255);


BEGIN

if (coalesce(ds_informacao_p,'0') <> '0') then
	begin
	ie_forma_inf_solic_w	:= coalesce(obter_valor_param_usuario(299, 499, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento),'C');

	if (ie_forma_inf_solic_w <> 'C') then
		begin
		if (ie_forma_inf_solic_w = 'P') then /* Número do CPF */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	nr_cpf = ds_informacao_p;
			end;
		elsif (ie_forma_inf_solic_w = 'I') then /* Número da identidade */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	nr_identidade = ds_informacao_p;
			end;
		elsif (ie_forma_inf_solic_w = 'F') then /* Código do funcionário/matrícula */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	cd_funcionario = ds_informacao_p;
			end;
		elsif (ie_forma_inf_solic_w = 'U') then /* Nome do usuário */
			begin
			select	cd_pessoa_fisica
			into STRICT	cd_pessoa_fisica_w
			from	usuario
			where	nm_usuario = ds_informacao_p;
			exception
			when others then
				cd_pessoa_fisica_w := null;
			end;
		end if;
		end;
	end if;

	if (coalesce(cd_pessoa_fisica_w,'0') = '0') then
		begin
		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = ds_informacao_p;
		exception
		when others then
			cd_pessoa_fisica_w := null;
		end;
	end if;
	end;
end if;

return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
