CREATE OR REPLACE FUNCTION man_obter_cor_tipo_os (ie_tipo_ordem_p bigint, ie_opcao_cor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_ordem_p IS NOT NULL AND ie_tipo_ordem_p::text <> '') then
	if (ie_opcao_cor_p	= 'U') then
		select	max(ds_cor_fundo)
		into STRICT	ds_retorno_w
		from	man_regra_cor_tipo_os
		where	ie_tipo_ordem	= ie_tipo_ordem_p
		and	ie_situacao = 'A';
	elsif (ie_opcao_cor_p = 'F') then
		select	max(ds_cor_fonte)
		into STRICT	ds_retorno_w
		from	man_regra_cor_tipo_os
		where	ie_tipo_ordem	= ie_tipo_ordem_p
		and	ie_situacao = 'A';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
