CREATE OR REPLACE FUNCTION man_obter_dados_acordo (dt_acordo_p timestamp, nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_dias_w		bigint := 7;


BEGIN

/* Retorno
	A - Passou do prazo
	P - Pr√≥ximo do prazo
	D - Dentro do prazo
*/
qt_dias_w := obter_param_usuario(296, 45, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, qt_dias_w);

if (dt_acordo_p IS NOT NULL AND dt_acordo_p::text <> '') then
	if	((trunc(dt_acordo_p) - qt_dias_w) >= trunc(clock_timestamp())) then
		ie_retorno_w	:= 'P';
	elsif (trunc(dt_acordo_p) > trunc(clock_timestamp())) then
		ie_retorno_w	:= 'D';
	elsif (trunc(dt_acordo_p) < trunc(clock_timestamp())) then
		ie_retorno_w	:= 'A';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
