CREATE OR REPLACE FUNCTION man_obter_dados_catal_servico ( nr_seq_servico_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
ie_opcao_p

D - Descrição
C - Código
DT - Descrição do tipo do catálogo
CT - Código do tipo
H - Número de horas
*/
cd_servico_w			varchar(80);
ds_tipo_servico_w		varchar(255);
nr_seq_tipo_servico_w	bigint;
qt_hora_util_w			double precision;
ds_servico_w			varchar(255);
ds_retorno_w			varchar(255);


BEGIN
select	cd_servico,
		substr(obter_descricao_padrao('MAN_TIPO_CATALOGO','DS_TIPO',nr_seq_tipo),1,255) ds_tipo_servico,
		nr_seq_tipo nr_seq_tipo_servico,
		qt_hora_util,
		ds_servico
into STRICT	cd_servico_w,
		ds_tipo_servico_w,
		nr_seq_tipo_servico_w,
		qt_hora_util_w,
		ds_servico_w
from	man_catalogo_servico
where	nr_sequencia = nr_seq_servico_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_servico_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := cd_servico_w;
elsif (ie_opcao_p = 'DT') then
	ds_retorno_w := ds_tipo_servico_w;
elsif (ie_opcao_p = 'CT') then
	ds_retorno_w := nr_seq_tipo_servico_w;
elsif (ie_opcao_p = 'H') then
	ds_retorno_w := qt_hora_util_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
