CREATE OR REPLACE FUNCTION man_obter_dados_classif ( nr_seq_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_classificacao_w	varchar(255);


BEGIN
select	substr(obter_desc_expressao(cd_exp_classificacao,ds_classificacao),1,255)
into STRICT	ds_classificacao_w
from	man_classif_gestao
where	nr_sequencia	= nr_seq_classif_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_classificacao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
