SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_dados_equipa_date ( nr_seq_equipamento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
dt_aquisicao_w			timestamp;
dt_inicio_garantia_w		timestamp;
dt_fim_garantia_w		timestamp;

/*ie_opcao_p
DA - data de aquisição
IG - Início Garantia
FG - Fim Garantia
*/
BEGIN

if (ie_opcao_p = 'DA') then
	select	max(dt_aquisicao)
	into STRICT	dt_aquisicao_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

	dt_retorno_w := dt_aquisicao_w;
elsif (ie_opcao_p = 'IG') then
	select	max(dt_inicio_garantia)
	into STRICT	dt_inicio_garantia_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

	dt_retorno_w := dt_inicio_garantia_w;
elsif (ie_opcao_p = 'FG') then
	select	max(dt_fim_garantia)
	into STRICT	dt_fim_garantia_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

	dt_retorno_w := dt_fim_garantia_w;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_dados_equipa_date ( nr_seq_equipamento_p bigint, ie_opcao_p text) FROM PUBLIC;

