CREATE OR REPLACE FUNCTION man_obter_dados_equip_html5 ( nr_seq_equipamento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;

/*ie_opcao_p

IG - In√≠cio Garantia
FG - Fim Garantia
*/
BEGIN

if (ie_opcao_p = 'FG') then
	select	max(dt_fim_garantia)
	into STRICT	dt_retorno_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

elsif (ie_opcao_p = 'IG') then
	select	max(dt_inicio_garantia)
	into STRICT	dt_retorno_w
	from	man_equipamento a
	where	nr_sequencia = nr_seq_equipamento_p;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
