CREATE OR REPLACE FUNCTION man_obter_dados_estagio ( nr_seq_estagio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
'D' - Descrição
'C' - Cor fonte
'F' - Cor fundo
'X' - Explicacao
*/
ds_retorno_w		varchar(255);
ds_estagio_w		varchar(40);
ds_cor_fonte_w		varchar(15);
ds_cor_fundo_w		varchar(15);
ds_explicacao_w		varchar(4000);


BEGIN
/*select	substr(obter_desc_expressao(cd_exp_estagio,ds_estagio),1,40),
	ds_cor_fonte,
	ds_cor_fundo,
	substr(obter_desc_expressao(cd_exp_explic_estagio,ds_explicacao),1,40)
into	ds_estagio_w,
	ds_cor_fonte_w,
	ds_cor_fundo_w,
	ds_explicacao_w
from	man_estagio_processo
where	nr_sequencia = nr_seq_estagio_p;*/
if (ie_opcao_p = 'D') then
	select	substr(obter_desc_expressao(cd_exp_estagio,ds_estagio),1,40)
	into STRICT	ds_estagio_w
	from	man_estagio_processo
	where	nr_sequencia = nr_seq_estagio_p;

	ds_retorno_w	:= ds_estagio_w;
elsif (ie_opcao_p = 'C') then
	select	ds_cor_fonte
	into STRICT	ds_cor_fonte_w
	from	man_estagio_processo
	where	nr_sequencia = nr_seq_estagio_p;

	ds_retorno_w	:= ds_cor_fonte_w;
elsif (ie_opcao_p = 'F') then
	select	ds_cor_fundo
	into STRICT	ds_cor_fundo_w
	from	man_estagio_processo
	where	nr_sequencia = nr_seq_estagio_p;

	ds_retorno_w	:= ds_cor_fundo_w;
elsif (ie_opcao_p = 'X') then
	select	substr(obter_desc_expressao(cd_exp_explic_estagio,ds_explicacao),1,40)
	into STRICT	ds_explicacao_w
	from	man_estagio_processo
	where	nr_sequencia = nr_seq_estagio_p;

	ds_retorno_w	:= substr(ds_explicacao_w,1,255);
end if;

return ds_retorno_w;

end;
$body$
