SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_dados_insp_result ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
'U' - Unidade de medida
'VE' - Valor esperado
'VI' - Valor minimo
'VA' - Valor maximo
'L' - Limite
'P' - Precisao
*/
ds_retorno_w		varchar(255);
ds_unidade_medida_w	varchar(255);
vl_esperado_w		man_inspecao_resultado.vl_esperado%Type;
vl_minimo_w		    man_inspecao_resultado.vl_minimo%Type;
vl_maximo_w		    man_inspecao_resultado.vl_maximo%Type;
ie_limite_w		    man_inspecao_resultado.ie_limite%Type;
ie_precisao_w		man_inspecao_resultado.ie_precisao%Type;
ds_resultado_w		man_inspecao_resultado.ds_resultado%Type;



BEGIN

select	substr(obter_descricao_padrao('MAN_INSPECAO_UNID_MED','DS_UNIDADE_MEDIDA',nr_seq_unid_med),1,255),
	vl_esperado,
	vl_minimo,
	vl_maximo,
	ie_limite,
	ie_precisao,
    ds_resultado
into STRICT	ds_unidade_medida_w,
	vl_esperado_w,
	vl_minimo_w,
	vl_maximo_w,
	ie_limite_w,
	ie_precisao_w,
    ds_resultado_w
from	man_inspecao_resultado
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'U') then
	ds_retorno_w	:= ds_unidade_medida_w;
elsif (ie_opcao_p = 'VE') then
	ds_retorno_w	:= vl_esperado_w;
elsif (ie_opcao_p = 'VI') then
	ds_retorno_w	:= vl_minimo_w;
elsif (ie_opcao_p = 'VA') then
	ds_retorno_w	:= vl_maximo_w;
elsif (ie_opcao_p = 'L') then
	ds_retorno_w	:= ie_limite_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w	:= ie_precisao_w;
elsif (ie_opcao_p = 'R') then
	ds_retorno_w	:= ds_resultado_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
