SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_dados_planej_prev ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_planejamento_w		varchar(80);
nr_seq_frequencia_w	bigint;

/*	ie_opcao_p
	D - Descrição
	F - Frequencia
	DF - Descrição frequencia
	UP - Última preventiva gerada	*/
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	ds_planejamento,
		nr_seq_frequencia
	into STRICT	ds_planejamento_w,
		nr_seq_frequencia_w
	from	man_planej_prev
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_planejamento_w;
	elsif (ie_opcao_p = 'F') then
		ds_retorno_w	:= nr_seq_frequencia_w;
	elsif (ie_opcao_p = 'DF') then
		select	ds_frequencia
		into STRICT	ds_retorno_w
		from	man_freq_planej
		where	nr_sequencia = nr_seq_frequencia_w;
	elsif (ie_opcao_p = 'UP') then
		begin
		select	max(nr_seq_ordem_servico)
		into STRICT	ds_retorno_w
		from	man_planej_prev_log
		where	ie_status = 'G'
		and	nr_seq_planej = nr_sequencia_p;

		if (coalesce(ds_retorno_w,'0') <> '0') then
			select	to_char(dt_ordem_servico,'dd/mm/yyyy')
			into STRICT	ds_retorno_w
			from	man_ordem_servico
			where	nr_sequencia = ds_retorno_w;
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
