CREATE OR REPLACE FUNCTION man_obter_dados_plano_insp ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
UR - Ultima rota de inspecao
*/
ds_retorno_w		varchar(255);


BEGIN

if (ie_opcao_p = 'UR') then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	ds_retorno_w
	from	man_rota_inspecao
	where	nr_seq_plano_inspecao = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
