SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_dados_reg_parada_os ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
L	Data de Liberação
O	Número da OS
*/
ds_retorno_w	varchar(255);


BEGIN
ds_retorno_w	:= '';
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	if (ie_opcao_p	= 'L') then
		select	to_char(max(a.dt_liberacao),'dd/mm/yyyy')
		into STRICT	ds_retorno_w
		from	man_ordem_servico_parada a
		where	a.nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'O') then
		select	max(a.nr_seq_ordem_serv)
		into STRICT	ds_retorno_w
		from	man_ordem_servico_parada a
		where	a.nr_sequencia	= nr_sequencia_p;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
