CREATE OR REPLACE FUNCTION man_obter_dados_rel_os_dia ( nr_grupo_trabalho_p bigint, dt_dia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
1 - Abertas
2 - Encerradas
3 - Total encerrada mês grupo
4 - Total encerrada dia semana grupo
5 - Média encerrada dia por grupo
6 - Total aberta mês grupo
7 - Total aberta dia semana grupo
8 - Média aberta dia por grupo
*/
/*
Function utilizada no relatório CMAN391 - Ordens de serviço diárias
*/
qt_aberta_w	bigint;
qt_encerrada_w	bigint;
qt_retorno_w	double precision;


BEGIN

select	coalesce(sum(qt_encerrada),0),
	coalesce(sum(qt_aberta),0)
into STRICT	qt_encerrada_w,
	qt_aberta_w
from	w_man_relat_os_diaria
where	dt_dia = dt_dia_p
and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0);

if (ie_opcao_p = '1') then
	qt_retorno_w	:= qt_aberta_w;
elsif (ie_opcao_p = '2') then
	qt_retorno_w	:= qt_encerrada_w;
elsif (ie_opcao_p = '3') then
	select	coalesce(sum(qt_encerrada),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = dt_dia_p
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0);
elsif (ie_opcao_p = '4') then
	select	coalesce(sum(qt_encerrada),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = trunc(dt_dia_p,'mm')
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0)
	and	ie_dia_semana = 'S';
elsif (ie_opcao_p = '5') then
	select	coalesce(avg(qt_encerrada),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = trunc(dt_dia_p,'mm')
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0)
	and	ie_dia_semana = 'S';
elsif (ie_opcao_p = '6') then
	select	coalesce(sum(qt_aberta),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = dt_dia_p
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0);
elsif (ie_opcao_p = '7') then
	select	coalesce(sum(qt_aberta),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = trunc(dt_dia_p,'mm')
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0)
	and	ie_dia_semana = 'S';
elsif (ie_opcao_p = '8') then
	select	coalesce(avg(qt_aberta),0)
	into STRICT	qt_retorno_w
	from	w_man_relat_os_diaria
	where	trunc(dt_dia,'mm') = trunc(dt_dia_p,'mm')
	and (nr_grupo_trabalho = coalesce(nr_grupo_trabalho_p,0) or coalesce(nr_grupo_trabalho_p,0) = 0)
	and	ie_dia_semana = 'S';
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
