SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_dados_rota_insp ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
DC - Data de criação
DI - Data de inicio
DT - Data de término
*/
ds_retorno_w		varchar(255);
dt_criacao_w		timestamp;
dt_inicio_w		timestamp;
dt_termino_w		timestamp;


BEGIN

select	dt_atualizacao_nrec,
	dt_inicio,
	dt_termino
into STRICT	dt_criacao_w,
	dt_inicio_w,
	dt_termino_w
from	man_rota_inspecao
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'DC') then
	ds_retorno_w := to_char(dt_criacao_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DI') then
	ds_retorno_w := to_char(dt_inicio_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DT') then
	ds_retorno_w := to_char(dt_termino_w,'dd/mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
