CREATE OR REPLACE FUNCTION man_obter_dados_tipo_os (nr_seq_tipo_ordem_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
'S' - situação do tipo

*/
ds_retorno_w	varchar(255);
ie_situacao_w	varchar(1);

BEGIN
select	max(coalesce(a.ie_situacao,'A'))
into STRICT	ie_situacao_w
from	man_tipo_ordem_servico a
where	a.nr_sequencia = nr_seq_tipo_ordem_p;

if (ie_opcao_p = 'S') then
	ds_retorno_w := ie_situacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
