CREATE OR REPLACE FUNCTION man_obter_dados_ult_prev ( nr_seq_planej_prev_p bigint, nr_seq_equipamento_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


dt_ordem_servico_w			timestamp;
nr_ordem_servico_w			bigint;
ds_retorno_w				varchar(255);

BEGIN

if (nr_seq_planej_prev_p IS NOT NULL AND nr_seq_planej_prev_p::text <> '') and (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') then
	select	max(dt_ordem_servico),
		max(nr_sequencia)
	into STRICT	dt_ordem_servico_w,
		nr_ordem_servico_w
	from	man_ordem_servico
	where	nr_seq_planej = nr_seq_planej_prev_p
	and	nr_seq_equipamento = nr_seq_equipamento_p;

	if (ie_informacao_p = 'N') then
		ds_retorno_w	:= to_char(nr_ordem_servico_w);
	elsif (ie_informacao_p = 'D') then
		ds_retorno_w	:= to_char(dt_ordem_servico_w,'dd/mm/yyyy');
	end if;
end if;

return ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
