CREATE OR REPLACE FUNCTION man_obter_data_prev_os (nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

dt_prevista_w	varchar(30);

BEGIN

select 	to_char(min(DT_PREVISTA),'dd/mm/yyyy hh24:mi:ss')
into STRICT	dt_prevista_w
from	MAN_ORDEM_ATIV_PREV a
where	nr_seq_ordem_p = a.nr_seq_ordem_serv
and	coalesce(DT_REAL::text, '') = ''
and	trunc(dt_prevista) >= trunc(clock_timestamp());

return	dt_prevista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
