CREATE OR REPLACE FUNCTION man_obter_data_ult_hist_tipo ( nr_seq_ordem_serv_p bigint, nr_seq_tipo_hist_p bigint, ie_philips_cliente_p text) RETURNS timestamp AS $body$
DECLARE


dt_historico_w	timestamp;


BEGIN

if (coalesce(nr_seq_tipo_hist_p,0) > 0) then
	begin
	select	max(dt_historico)
	into STRICT	dt_historico_w
	from	man_ordem_serv_tecnico
	where	nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and	nr_seq_tipo = nr_seq_tipo_hist_p
	and	((ie_philips_cliente_p = 'P' and upper(nm_usuario) <> 'WEBSERVICE') or (ie_philips_cliente_p = 'C' and upper(nm_usuario) = 'WEBSERVICE'));
	end;
else
	begin
	select	max(dt_historico)
	into STRICT	dt_historico_w
	from	man_ordem_serv_tecnico
	where	nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and	((ie_philips_cliente_p = 'P' and upper(nm_usuario) <> 'WEBSERVICE') or (ie_philips_cliente_p = 'C' and upper(nm_usuario) = 'WEBSERVICE'));
	end;
end if;

return	dt_historico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
