CREATE OR REPLACE FUNCTION man_obter_desc_equipamento ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_equipamento_w				varchar(100);
nr_Seq_superior_w				bigint;


BEGIN

select	substr(max(ds_equipamento),1,80),
	max(nr_seq_superior)
into STRICT	ds_equipamento_w,
	nr_seq_superior_w
from	man_equipamento
where	nr_sequencia = nr_sequencia_p;

if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
	ds_equipamento_w	:= '      ' || ds_equipamento_w;
end if;

return ds_equipamento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
