CREATE OR REPLACE FUNCTION man_obter_desc_estagio_idioma ( nr_seq_estagio_p bigint, nr_seq_idioma_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_estagio_w		varchar(255);


BEGIN

	select	substr(obter_desc_expressao_idioma(cd_exp_estagio, ds_estagio, nr_seq_idioma_p), 1, 255)
	into STRICT	ds_estagio_w
	from	man_estagio_processo
	where	nr_sequencia = nr_seq_estagio_p;

return ds_estagio_w;

end;
