CREATE OR REPLACE FUNCTION man_obter_desc_subtipo_erro_os ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_subtipo_w	varchar(80);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	substr(obter_desc_expressao(cd_exp_subtipo, ds_subtipo), 1,200) ds
	into STRICT	ds_subtipo_w
	from	man_subtipo_erro_os
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_subtipo_w;

end;
$body$
