CREATE OR REPLACE FUNCTION man_obter_desc_unidade_medida ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_unidade_medida_w	unidade_medida.ds_unidade_medida%type;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(a.ds_unidade_medida)
	into STRICT	ds_unidade_medida_w
	from	unidade_medida a,
		man_equip_calib_escala b
	where	a.cd_unidade_medida = b.cd_unidade_medida
	and 	b.nr_sequencia = nr_sequencia_p;
end if;

return	ds_unidade_medida_w;

end;
$body$
