SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_determ_risco ( ie_probability_harm_p text, ie_severity_harm_p text ) RETURNS varchar AS $body$
DECLARE

ds_risco_w 	varchar(255);
qt_rules_w      bigint;
ie_acceptable_w	varchar(1);

BEGIN

        select  count(1)
        into STRICT    qt_rules_w
        from    man_regra_determ_risco
        where   ie_situacao = 'A';

        if (qt_rules_w > 0) then
                ie_acceptable_w := man_obter_regra_determ_risco(ie_probability_harm_p, ie_severity_harm_p);
		
		if (ie_acceptable_w = 'I') then
			ds_risco_w 	:= obter_desc_expressao_idioma(291775, 'Unacceptable', 5);
		elsif (ie_acceptable_w = 'N') then
			ds_risco_w 	:= obter_desc_expressao_idioma(951217, 'Further Analysis Required', 5);
		elsif (ie_acceptable_w = 'A') then
			ds_risco_w 	:= obter_desc_expressao_idioma(283120, 'Acceptable', 5);
		elsif (coalesce(ie_acceptable_w::text, '') = '') then
			ds_risco_w 	:= obter_desc_expressao_idioma(344145, 'N/A', 5);
		end if;
        else
                if (coalesce(ie_probability_harm_p::text, '') = '' or coalesce(ie_severity_harm_p::text, '') = '') then
                        ds_risco_w 	:= obter_desc_expressao_idioma(344145, 'N/A', 5);
                elsif (ie_probability_harm_p + ie_severity_harm_p > 4) then
                        ds_risco_w 	:= obter_desc_expressao_idioma(291775, 'Unacceptable', 5);
                elsif ( (ie_probability_harm_p = 2 and ie_severity_harm_p = 2) or (ie_probability_harm_p = 1 and ie_severity_harm_p = 3) ) then
                        ds_risco_w 	:= obter_desc_expressao_idioma(951217, 'Further Analysis Required', 5);
                else
                        ds_risco_w 	:= obter_desc_expressao_idioma(283120, 'Acceptable', 5);
                end if;
        end if;
	
	return ds_risco_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
