CREATE OR REPLACE FUNCTION man_obter_dias_os_desenv (nr_seq_ordem_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_os_desenv_w	integer := 0;


BEGIN
/*Esta function foi criada para obter a quantidade total de dias em desenvolvimento das ordens de serviço.
A function obter_tempo_os_desenv, retorna apenas a quantidade de dias, quando o último estágio da OS está em desenvolvimento. */
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	select  round(sum(qt_minutos_estagio / 1440)) qt_dias_estagio
	into STRICT	qt_dias_os_desenv_w
	from (	SELECT     man_obter_tempo_estagio(a.nr_sequencia,a.nr_seq_ordem,'M') qt_minutos_estagio
	from    man_estagio_processo b,
		man_ordem_serv_estagio a
	where   a.nr_seq_ordem          = nr_seq_ordem_p
	and     b.nr_sequencia          = a.nr_seq_estagio
	and	b.nr_sequencia = 4) alias4;
end if;

return	qt_dias_os_desenv_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
